mapper.select:

    SELECT DISTINCT M.*,F.F_SVNAME
    FROM MOIM M, FILE_T F
    WHERE F.F_DEL_YN(+) = 'N'
    AND F.F_MAIN_YN(+) = 'Y' 
    AND M.MO_DEL_YN = 'N' 
    AND F.F_TABLE(+) = 'M'


			AND (M.MO_CATEGORY = #{MO_CATEGORY})







								M.MO_REGION = #{MO_REGION}



									M.MO_REGDATE = #{MO_REGDATE}


									M.MO_DEADTIME BETWEEN #{MO_REGDATE} AND #{MO_REGDATE}




									M.MO_DEADTIME = #{MO_DEADTIME}


									M.MO_DEADTIME BETWEEN #{MO_DEADTIME} AND
									#{MO_DEADTIME}







		ORDER BY M.MO_CLOSE_YN, M.MO_REGDATE DESC


    SELECT COUNT(*) 
    FROM MOIM M, FILE_T F
    WHERE F.F_DEL_YN(+) = 'N'
    AND F.F_MAIN_YN(+) = 'Y' 
    AND M.MO_DEL_YN = 'N' 
    AND F.F_TABLE(+) = 'M'


			AND (M.MO_CATEGORY = #{MO_CATEGORY})







								M.MO_REGION = #{MO_REGION}



									M.MO_REGDATE = #{MO_REGDATE}


									M.MO_DEADTIME BETWEEN #{MO_REGDATE} AND #{MO_REGDATE}




									M.MO_DEADTIME = #{MO_DEADTIME}


									M.MO_DEADTIME BETWEEN #{MO_DEADTIME} AND
									#{MO_DEADTIME}

mapper.update:

UPDATE MOIM SET
       MO_CLOSE_YN = 'Y'
WHERE MO_IDX = #{MO_IDX}


  UPDATE MOIM SET
   MO_DEL_YN = 'Y'
WHERE MO_IDX = #{MO_IDX}
